(margin|padding|border)-/', $name))) { unset($baseStyles[$name]); } elseif (($name === 'background-size' && $value === 'auto') || ($name === 'background-position' && $value === 'left top') || ($name === 'background-repeat' && $value === 'no-repeat')) { unset($baseStyles[$name]); } } if (empty($baseStyles['border-style']) || $baseStyles['border-style'] === 'none') { unset($baseStyles['border-color']); unset($baseStyles['border-left-width'], $baseStyles['border-right-width'], $baseStyles['border-top-width'], $baseStyles['border-bottom-width']); } if (empty($baseStyles['background-image']) || $baseStyles['background-image'] === '') { unset($baseStyles['background-size'], $baseStyles['background-position'], $baseStyles['background-repeat']); } $properties['desktop']['base'] = $baseStyles; $item->properties = json_encode($properties); $table->save($item); } } } catch (\Exception $e) { return false; } return true; } public static function upgradeDatabaseTableCollate() { $tables = [ 'content_blocks', 'fonts', 'languages', 'media_folders', 'media_items', 'menus', 'menu_items', 'pages', 'pages_treepath', 'roles', 'settings', 'styles', 'users', 'presets', ]; $prefix = static::_getTablePrefix(); $databaseAdapter = static::_getDatabaseAdapter(); Moto\System\Log::info('@UPDATE Database : setup default CHARACTER "utf8" and COLLATE "utf8_unicode_ci"'); foreach ($tables as $tableName) { try { $sql = 'ALTER TABLE `' . $prefix . $tableName . '` CONVERT TO CHARACTER SET utf8 COLLATE utf8_unicode_ci;'; $databaseAdapter->query($sql)->execute(); } catch (\Exception $e) { Moto\System\Log::critical('@UPDATE Database : Error : ' . $e->getMessage()); throw $e; } } } } 