<?php
namespace Website\Widgets\Block; use Moto; class Widget extends Moto\System\Widgets\AbstractContainerWidget { protected $_name = 'block'; protected static $_defaultProperties = array( 'anchor' => '', 'stickyEnabled' => false, 'styles' => array( 'background-color' => '', ), 'fixed' => false, 'bgFixed' => false, 'bgParallaxed' => false, 'spacing' => array( 'top' => 'auto', 'right' => 'auto', 'bottom' => 'auto', 'left' => 'auto', ), ); protected $_templateType = 'templates'; protected $_widgetId = true; public $customAttributes = []; public function getCustomAttributes() { return $this->customAttributes; } public function preRender() { parent::preRender(); if ($this->getPropertyValue('stickyEnabled')) { $this->customAttributes['data-moto-sticky'] = '{}'; } if ($this->getPropertyValue('bgParallaxed')) { $this->customAttributes['data-stellar-background-ratio'] = '0.5'; } } public function getCssClasses() { $result = ''; if ($this->getPropertyValue('fixed', false)) { $result .= ' row-fixed'; } if ($this->getPropertyValue('styles.background-color', false)) { $result .= ' ' . $this->getCssClassColor($this->getPropertyValue('styles.background-color'), 'moto-bg-'); } $result .= ' ' . $this->getSpacing('classes'); return $result; } public function getStylesValue() { return $this->getPropertyValue('styles'); } public function _getBackgroundVideoCssClass() { return 'moto-background-video moto-absolute-position'; } } 