<?php
 namespace Website\Widgets\Blog; use Moto; use Website; class TagDescriptionWidget extends Website\Widgets\Blog\AbstractPostsWidget { protected $_name = 'blog.tag_description'; protected static $_defaultProperties = array( 'source' => 'short_description', 'firstPageOnly' => false, 'visible_on' => 'mobile-v', 'spacing' => array( 'top' => 'auto', 'right' => 'auto', 'bottom' => 'auto', 'left' => 'auto', ), ); public function getTemplatePath($preset = null) { return '@websiteWidgets/blog/templates/tag_description.twig.html'; } public function shouldBeRendered() { return !($this->getPropertyValue('firstPageOnly', false) && Moto\System\Request::getQuery('page', 1) != 1); } } 